recipeName('528', 'beer can chicken'). 
picture('528', 'https://mobkitchen-objects.imgix.net/recipes/2020/in-line-option-2.jpg?auto=format&crop=focalpoint&domain=mobkitchen-objects.imgix.net&fit=crop&fp-x=0.5&fp-y=0.5&h=827&ixlib=php-3.3.1&q=82&w=1300&s=bb780e0b50ad312388a29f786e6d1216'). 
time('528', 90). 
servings('528', 4). 
cuisine('528', 'british'). 
mealType('528', 'dinner'). 
mealType('528', 'dinnerparty'). 
step('528', 1, 'preheat the oven to 190c/375f.'). 
step('528', 2, 'make the rub by combining the cumin, cayenne, oregano, sugar, garlic, and 2 tbsp olive oil in a bowl.'). 
step('528', 3, 'add a big pinch of salt and grind of pepper.'). 
step('528', 4, 'rub the mixture onto the inside and outside of the bird.'). 
step('528', 5, 'pour your chicken stock into the empty can.'). 
step('528', 6, 'stuff the can into the cavity of the chicken, then place the whole thing on a big roasting tin so that its legs are pointing down.'). 
step('528', 7, 'put the chicken into the oven and roast for one hour and fifteen minutes.'). 
step('528', 8, 'take it out, remove and set aside the can, then leave your chicken to rest on a chopping board for as long as it takes to make your gravy.'). 
